import React, { useState } from 'react';
import { View, TextInput, TouchableOpacity, SafeAreaView, ImageBackground, Text, ActivityIndicator, KeyboardAvoidingView, Platform } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useAuth } from '../context/AuthContext';
import { useNavigation } from '@react-navigation/native';
import Toast from 'react-native-toast-message';
import styles from '../../styles/Login.style';
import useNetworkStatus from '../../components/NetworkStatus/NetworkStatus';

const Login = () => {
  const [num_tel, setNumTel] = useState('');
  const [password, setPassword] = useState('');
  const [secureText, setSecureText] = useState(true);
  const [loading, setLoading] = useState(false);
  const { onLogin } = useAuth();
  const navigation = useNavigation();
  const isOnline = useNetworkStatus();

  const togglePasswordVisibility = () => setSecureText(!secureText);

  const login = async () => {
    // Vérifier la connectivité réseau
    if (!isOnline) {
      Toast.show({
        type: 'error',
        text1: 'Pas de connexion internet',
        text2: 'Veuillez vérifier votre connexion et réessayer.',
      });
      return;
    }

    // Vérifier que tous les champs sont remplis (en supprimant les espaces inutiles)
    if (!num_tel.trim() || !password.trim()) {
      Toast.show({
        type: 'error',
        text1: 'Champs obligatoires',
        text2: 'Tous les champs sont obligatoires.',
      });
      return;
    }

    setLoading(true);
    try {
      const result = await onLogin(num_tel.trim(), password);
      if (result?.error) {
        // Affichage du message générique en cas d'erreur sur le numéro ou le mot de passe
        Toast.show({
          type: 'error',
          text1: 'Erreur',
          text2: "Une erreur inattendue s'est produite.",
        });
        console.log('====================================');
        console.log();
        console.log('====================================');
      } else {
        navigation.navigate('Home');
      }
    } catch (error) {
      Toast.show({
        type: 'error',
        text1: 'Erreur',
        text2: "Une erreur inattendue s'est produite.",
      });
      console.error('Erreur lors de la connexion :', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <SafeAreaView style={{ flex: 1 }}>
      <KeyboardAvoidingView style={{ flex: 1 }} behavior={Platform.OS === 'ios' ? 'padding' : undefined}>
        <View style={styles.container}>
          <ImageBackground source={require('../../assets/images/city.jpg')} style={styles.head} />
          <View style={styles.formLogin}>
            <TextInput
              style={styles.inputText}
              placeholder="Numéro de téléphone"
              onChangeText={setNumTel}
              value={num_tel}
              keyboardType="phone-pad"
              autoCapitalize="none"
              autoCorrect={false}
            />
            <View style={styles.inputPassword}>
              <TextInput
                style={styles.input}
                placeholder="Mot de passe"
                secureTextEntry={secureText}
                onChangeText={setPassword}
                value={password}
                autoCapitalize="none"
                autoCorrect={false}
              />
              <TouchableOpacity onPress={togglePasswordVisibility} accessible accessibilityLabel="Afficher/Masquer le mot de passe">
                <Ionicons name={secureText ? 'eye-outline' : 'eye-off-outline'} size={24} color="gray" style={styles.icon} />
              </TouchableOpacity>
            </View>
            <TouchableOpacity style={styles.btnSubmit} onPress={login} disabled={loading}>
              <Text style={styles.btnSubmitText}>Se Connecter</Text>
            </TouchableOpacity>
            {loading && <ActivityIndicator size="large" color="#0000ff" style={{ marginTop: 10 }} />}
          </View>
        </View>
      </KeyboardAvoidingView>
    </SafeAreaView>
  );
};

export default Login;
