// Login.tsx
import { useState } from 'react';
import { View, TextInput, TouchableOpacity, SafeAreaView, ImageBackground, Text, ActivityIndicator, KeyboardAvoidingView } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useAuth } from '../context/AuthContext';
import { useNavigation } from "@react-navigation/native";
import styles from '../../styles/Login.style';
import useNetworkStatus from '../../components/NetworkStatus/NetworkStatus';
import Toast from 'react-native-toast-message';

const Login = () => {
  const [num_tel, setNumTel] = useState('');
  const [password, setPassword] = useState('');
  const [secureText, setSecureText] = useState(true);
  const [loading, setLoading] = useState(false);
  const { onLogin, authState, getRole } = useAuth();
  const navigation = useNavigation();
  const isOnline = useNetworkStatus();

  const togglePasswordVisibility = () => setSecureText(!secureText);

  const login = async () => {
    if (!num_tel.trim() || !password.trim()) {
      Toast.show({
        type: 'error',
        text1: 'Tous les champs sont obligatoires',
        text2: 'Veuillez réessayer!'
      });
      return;
    }

    setLoading(true);
    const result = await onLogin(num_tel, password);
    setLoading(false);

    if (result?.error) {
      Toast.show({
        type: 'error',
        text1: 'Utilisateur inexistant ou mot de passe incorrect',
        text2: 'Veuillez réessayer!'
      });
    } else {
      try {
        const role = await getRole(authState.token);
        if (role !== 'client') {
          navigation.navigate("Home");
          Toast.show({
            type: 'Su',
            text1: 'Accès refusé',
            text2: 'Votre rôle client ne permet pas l\'accès à cette interface.'
          });
        } else {
        }
      } catch (error) {
        console.error("❌ Erreur lors de la récupération du rôle :", error);
        Toast.show({
          type: 'error',
          text1: 'Erreur',
          text2: "Erreur lors de la récupération du rôle"
        });
      }
    }
  };

  return (
    <SafeAreaView>
      <KeyboardAvoidingView style={styles.container}>
        <ImageBackground source={require('../../assets/images/city.jpg')} style={styles.head} />
        <View style={styles.formLogin}>
          <TextInput
            style={styles.inputText}
            placeholder="Numéro de téléphone"
            onChangeText={setNumTel}
            value={num_tel}
            keyboardType="phone-pad"
          />
          <View style={styles.inputPassword}>
            <TextInput
              style={styles.input}
              placeholder="Mot de passe"
              secureTextEntry={secureText}
              onChangeText={setPassword}
              value={password}
            />
            <TouchableOpacity onPress={togglePasswordVisibility}>
              <Ionicons name={secureText ? "eye-outline" : "eye-off-outline"} size={24} color="gray" style={styles.icon} />
            </TouchableOpacity>
          </View>
          <TouchableOpacity style={styles.btnSubmit} onPress={login} disabled={loading}>
            <Text style={styles.btnSubmitText}>Se Connecter</Text>
          </TouchableOpacity>
          {loading && <ActivityIndicator size="large" color="#0000ff" style={{ marginTop: 10 }} />}
        </View>
      </KeyboardAvoidingView>
    </SafeAreaView>
  );
};

export default Login;
